def quitar_acentos(palabra):
    acentos = ["á","é","í","ó","ú","ü"]
    letras= ["a","e","i","o","u","u"]
    for i in range(len(acentos)):
        palabra = palabra.replace(acentos[i],letras[i])
    return palabra

def formatear_datos(letras):
    diccionario_letras = {}
    acentos = ["á","é","í","ó","ú","ü"]
    for letra in letras:
        fichero = "palabras_{}.txt".format(letra)
        lector = open(fichero, "r")
        diccionario_letras[letra]=[]
        for linea in lector:
            palabra = linea[:-1]
            hay_acentos = True in [acent in palabra for acent in acentos]
            longitud_palabra = len(palabra)
            if (longitud_palabra == 5 or longitud_palabra == 4) and " " not in palabra and not hay_acentos:
                diccionario_letras[letra].append(palabra)
            #if longitud_palabra in diccionario_letras[letra].keys():
            #    diccionario_letras[letra][longitud_palabra].append(palabra)

            #else:
            #    diccionario_letras[letra][longitud_palabra]= [palabra]

        lector.close()
    return diccionario_letras


def conseguir_palabras(letras,diccionario_letras):
    def conseguir_letra (letras):
        hora = int(time.time())
        letra = letras[hora%26]
        return letra
    def conseguir_palabra(letra):
        hora = int(time.time())
        vector_palabras_letra = diccionario_letras[letra]
        palabra = vector_palabras_letra[hora%len(vector_palabras_letra)]
        return palabra

    letra = conseguir_letra(letras)
    palabra = conseguir_palabra(letra)

    while len(palabra)==4:
            palabra = conseguir_palabra(letra)

    return quitar_acentos(palabra)


